--  DDL for Trigger COUNTRY_CONTINENT_ETRG
--------------------------------------------------------

  CREATE OR REPLACE TRIGGER "WORLD"."COUNTRY_CONTINENT_ETRG" 
BEFORE INSERT OR UPDATE ON "WORLD"."COUNTRY"
FOR EACH ROW
DECLARE
  v_val country.Continent%TYPE;
  v_test country.Continent%TYPE;
BEGIN
  v_val := 'Asia';
  v_test := TRIM(:new.Continent);
  if v_test = '1' OR v_test = 'Asia' THEN 
  v_val := 'Asia';
  elsif v_test = '2' OR v_test = 'Europe' THEN 
  v_val := 'Europe';
  elsif v_test = '3' OR v_test = 'North America' THEN 
  v_val := 'North America';
  elsif v_test = '4' OR v_test = 'Africa' THEN 
  v_val := 'Africa';
  elsif v_test = '5' OR v_test = 'Oceania' THEN 
  v_val := 'Oceania';
  elsif v_test = '6' OR v_test = 'Antarctica' THEN 
  v_val := 'Antarctica';
  elsif v_test = '7' OR v_test = 'South America' THEN 
  v_val := 'South America';
  end if;  :new.Continent := v_val;
END;
/
ALTER TRIGGER "WORLD"."COUNTRY_CONTINENT_ETRG" ENABLE;
